/**
 * 异常工厂
 * @remarks
 * 用于异常信息管理和异常信息处理
 */
declare class ErrorFactory {
    /**
     * 异常信息map，键为异常码，值为异常信息
     */
    static errMap: Map<string, string>;
    /**
     * 异常提示语言
     */
    /**
     * 获取异常
     * @param errNo     异常码
     * @param param     参数值数组，用于处理消息带参数的情况
     * @returns         {code:异常码,message:异常信息}
     */
    static getError(errNo: string, param?: Array<any>): Error;
}
export { ErrorFactory };
