/**
 * 日志类
 * 采用log4js进行日志管理
 * @since 1.0.0
 */
declare class Logger {
    /**
     * log4js 对象
     */
    private static log4js;
    /**
     * 初始化日志管理器
     * @param debug     是否开启dubug模式
     * @param fileLog   是否开启文件日志
     */
    static init(debug: boolean, fileLog: boolean): void;
    /**
     * 写日志到控制台
     * @param sql       sql语句
     * @param params    sql语句参数
     */
    static log(sql: string, params?: any[] | object): void;
    /**
     * 写错误消息
     * @param err   sql执行错误
     */
    static error(err: Error): void;
}
export { Logger };
