/**
 * relaen 框架管理器
 */
declare class RelaenManager {
    /**
     * 数据库类型
     * mysql oracle mssql postgres mariadb sqlite
     */
    static dialect: "mysql" | "oracle" | "mssql" | "postgres" | "mariadb" | "sqlite";
    /**
     * 开启一级cache
     */
    static cache: boolean;
    /**
     * 是否调试模式
     */
    static debug: boolean;
    /**
     * 是否文件日志
     * @since 0.4.0
     */
    static fileLog: boolean;
    /**
     * 是否允许全表操作，默认禁止
     * @since 0.4.0
     */
    static fullTableOperation: boolean;
    /**
     * 初始化
     * @param cfg   配置文件名或配置对象
     */
    static init(cfg: any): Promise<void>;
    /**
     * 初始化各dialect对应的translator
     */
    private static initTranslator;
    /**
     * 初始化各dialect对应的provider
     */
    private static initProvider;
    /**
     * 初始化各dialect对应的占位符配置
     */
    private static initPlaceholder;
    /**
     * 初始化各dialect对应的transaction
     */
    private static initTransaction;
}
export { RelaenManager };
